INSERT INTO public.services (title, description, price, category, is_active) VALUES
('Пиар ТГК/ресурса', 'Пиар вашего Telegram канала или другого ресурса на 2 недели в закреп. Первая покупка со скидкой 20% (1199₽). Средний прирост 30-100 подписчиков.', 1499.00, 'Реклама', true),
('Пиар розыгрыша', 'Реклама розыгрыша репостом на 2 недели в закреп. Первая покупка со скидкой 20% (1439₽). Средний прирост 400-800+ подписчиков.', 1799.00, 'Реклама', true),
('Пиар бизнеса', 'Реклама вашего бизнеса с индивидуальными условиями. Обсуждается отдельно с менеджером.', 6999.00, 'Реклама', true),
('Посредничество', 'Безопасное проведение сделок в танковой сфере и партнёрстве. Гарантия сохранности денег и проверка товара перед передачей.', 0.00, 'Услуги', true),
('SMM-менеджмент', 'Написание постов для вашего ТГК по танковой тематике по шаблону. Качественный контент от опытных специалистов.', 85.00, 'Услуги', true),
('Модерирование чата', 'Профессиональная модерация вашего чата до 8 часов в день. Опытный специалист будет следить за порядком и соблюдением правил.', 3999.00, 'Услуги', true),
('Малый карт-бланш (90 дней)', '3 бесплатных публикации розыгрышей + 50% скидка на пиар (1 раз в месяц). Срок действия 90 дней.', 1999.00, 'Подписки', true),
('Средний карт-бланш (180 дней)', '7 бесплатных публикаций розыгрышей + 65% скидка на пиар (1 раз в месяц). Срок действия 180 дней.', 2999.00, 'Подписки', true),
('Расширенный карт-бланш (365 дней)', '15 бесплатных публикаций розыгрышей + 75% скидка на пиар (1 раз в месяц) + доступ к инсайдерской информации. Срок действия 365 дней.', 3599.00, 'Подписки', true);
